import React, { useState } from 'react';
import {
  ChartBarIcon,
  GlobeAltIcon
} from '@heroicons/react/24/outline';
import RASTProjects from './RASTProjects';
import RASTDashboard from '../../components/RAST/RASTDashboard';

const RAST: React.FC = () => {
  const [activeTab, setActiveTab] = useState('overview');

  const tabs = [
    { id: 'overview', label: 'Overview', icon: <ChartBarIcon className="w-4 h-4" /> },
    { id: 'projects', label: 'Projects', icon: <GlobeAltIcon className="w-4 h-4" /> }
  ];

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Runtime Application Security Testing (RAST)</h1>
          <p className="text-gray-600">Monitor and protect applications during runtime execution</p>
        </div>
      </div>

      {/* Tabs */}
      <div className="border-b border-gray-200">
        <nav className="-mb-px flex space-x-8">
          {tabs.map((tab) => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              className={`flex items-center py-2 px-1 border-b-2 font-medium text-sm ${
                activeTab === tab.id
                  ? 'border-blue-500 text-blue-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              }`}
            >
              {tab.icon}
              <span className="ml-2">{tab.label}</span>
            </button>
          ))}
        </nav>
      </div>

      {/* Tab Content */}
      <div className="mt-6">
        {activeTab === 'overview' && <RASTDashboard />}
        {activeTab === 'projects' && <RASTProjects />}
      </div>
    </div>
  );
};

export default RAST;
