import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import {
  ArrowLeftIcon,
  PlayIcon,
  ClockIcon,
  ExclamationTriangleIcon,
  CheckCircleIcon,
  XCircleIcon,
  ShieldExclamationIcon,
  ChartBarIcon,
  CogIcon,
  DocumentTextIcon,
  EyeIcon,
  MagnifyingGlassIcon,
  FunnelIcon,
  ChevronDownIcon,
  ChevronUpIcon,
  StarIcon,
  CodeBracketIcon,
  BeakerIcon,
  ArrowPathIcon,
  BugAntIcon,
  Cog6ToothIcon,
  ChevronLeftIcon,
  XMarkIcon,
  CheckIcon,
  ExclamationCircleIcon,
  InformationCircleIcon,
} from '@heroicons/react/24/outline';

interface ProjectDetails {
  id: number;
  name: string;
  key: string;
  language: string;
  repositoryUrl?: string;
  branch: string;
  qualityGate: 'PASSED' | 'FAILED' | 'WARNING' | 'NONE';
  maintainabilityRating: 'A' | 'B' | 'C' | 'D' | 'E';
  securityRating: 'A' | 'B' | 'C' | 'D' | 'E';
  reliabilityRating: 'A' | 'B' | 'C' | 'D' | 'E';
  vulnerabilityCount: number;
  bugCount: number;
  codeSmellCount: number;
  securityHotspotCount: number;
  linesOfCode: number;
  coverage: number;
  technicalDebt: number;
  lastAnalysis?: string;
  lastScan?: {
    id: number;
    status: 'COMPLETED' | 'RUNNING' | 'FAILED' | 'PENDING';
    timestamp: string;
    duration?: string;
  };
}

interface Issue {
  id: number;
  key: string;
  type: 'BUG' | 'VULNERABILITY' | 'CODE_SMELL';
  severity: 'BLOCKER' | 'CRITICAL' | 'MAJOR' | 'MINOR' | 'INFO';
  status: 'OPEN' | 'CONFIRMED' | 'RESOLVED' | 'CLOSED' | 'REOPENED';
  resolution?: 'FIXED' | 'FALSE_POSITIVE' | 'WONT_FIX' | 'REMOVED';
  component: string;
  line: number;
  message: string;
  effort: string;
  debt: string;
  author: string;
  creationDate: string;
  updateDate: string;
  tags: string[];
}

interface IssueFilter {
  type: string[];
  severity: string[];
  status: string[];
  resolution: string[];
  author: string;
  component: string;
}

interface IssueSort {
  field: keyof Issue;
  direction: 'asc' | 'desc';
}

interface SecurityHotspot {
  id: number;
  key: string;
  component: string;
  line: number;
  message: string;
  status: 'TO_REVIEW' | 'REVIEWED' | 'RESOLVED';
  resolution?: 'SAFE' | 'FIXED' | 'ACKNOWLEDGED' | 'FALSE_POSITIVE';
  severity: 'HIGH' | 'MEDIUM' | 'LOW';
  category: 'SQL_INJECTION' | 'XSS' | 'CSRF' | 'PATH_TRAVERSAL' | 'COMMAND_INJECTION' | 'LDAP_INJECTION' | 'OPEN_REDIRECT' | 'WEAK_CRYPTOGRAPHY' | 'INSECURE_DESERIALIZATION' | 'OTHER';
  vulnerabilityProbability: 'HIGH' | 'MEDIUM' | 'LOW';
  securityCategory: 'OWASP_TOP_10' | 'CWE' | 'SANS_TOP_25' | 'OTHER';
  author: string;
  creationDate: string;
  updateDate: string;
  reviewer?: string;
  reviewDate?: string;
  comment?: string;
}

interface HotspotFilter {
  status: string[];
  severity: string[];
  category: string[];
  securityCategory: string[];
  vulnerabilityProbability: string[];
  author: string;
  component: string;
}

interface HotspotSort {
  field: keyof SecurityHotspot;
  direction: 'asc' | 'desc';
}

interface QualityGate {
  id: number;
  name: string;
  status: 'PASSED' | 'FAILED' | 'WARNING' | 'NONE';
  conditions: QualityGateCondition[];
  lastEvaluation?: string;
  nextEvaluation?: string;
}

interface QualityGateCondition {
  id: number;
  metric: string;
  operator: 'GT' | 'LT' | 'EQ' | 'NE';
  threshold: number;
  actualValue: number;
  status: 'PASSED' | 'FAILED' | 'WARNING' | 'NONE';
  description: string;
  category: 'RELIABILITY' | 'SECURITY' | 'MAINTAINABILITY' | 'COVERAGE' | 'DUPLICATIONS' | 'SIZE';
}

interface QualityGateThreshold {
  metric: string;
  operator: 'GT' | 'LT' | 'EQ' | 'NE';
  threshold: number;
  description: string;
  category: 'RELIABILITY' | 'SECURITY' | 'MAINTAINABILITY' | 'COVERAGE' | 'DUPLICATIONS' | 'SIZE';
}

interface CoverageData {
  overall: number;
  lines: number;
  functions: number;
  branches: number;
  statements: number;
  uncoveredLines: number[];
  uncoveredFunctions: string[];
  uncoveredBranches: string[];
  fileCoverage: FileCoverage[];
  trendData: CoverageTrend[];
}

interface FileCoverage {
  file: string;
  lines: number;
  coveredLines: number;
  coverage: number;
  uncoveredLines: number[];
  complexity: number;
  lastModified: string;
}

interface CoverageTrend {
  date: string;
  overall: number;
  lines: number;
  functions: number;
  branches: number;
  statements: number;
}

