import React, { useState, useEffect, useCallback } from 'react';
import { motion } from 'framer-motion';
import {
  ShieldCheckIcon,
  GlobeAltIcon,
  CogIcon,
  PlusIcon,
  EyeIcon,
  PencilIcon,
  TrashIcon,
  ExclamationTriangleIcon,
  KeyIcon,
  ServerIcon,
  CheckCircleIcon,
  ArrowPathIcon,
  XCircleIcon,
  ClockIcon,
  MagnifyingGlassIcon,
  PlayIcon,
  DocumentDuplicateIcon,
  ChevronLeftIcon,
  ChevronRightIcon
} from '@heroicons/react/24/outline';

interface RASTProject {
  id: number;
  name: string;
  url: string;
  description?: string;
  scanType: 'full' | 'passive' | 'active' | 'custom';
  authentication?: {
    type: 'none' | 'basic' | 'form' | 'token';
    username?: string;
    password?: string;
  };
  lastScan?: {
    id: number;
    status: 'COMPLETED' | 'RUNNING' | 'FAILED' | 'PENDING';
    timestamp: string;
    duration?: string;
  };
  vulnerabilities: {
    critical: number;
    high: number;
    medium: number;
    low: number;
  };
  securityScore: number;
  createdBy: string;
  createdAt: string;
}

interface CreateProjectData {
  name: string;
  url: string;
  description?: string;
  scanType: 'full' | 'passive' | 'active' | 'custom';
  authentication?: {
    type: 'none' | 'basic' | 'form' | 'token';
    username?: string;
    password?: string;
  };
}

interface DuplicateProjectData {
  name: string;
  url: string;
}

const RASTProjects: React.FC = () => {
  const [projects, setProjects] = useState<RASTProject[]>([]);
  const [loading, setLoading] = useState(true);
  const [viewMode, setViewMode] = useState<'list' | 'cards'>('cards');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedScanType, setSelectedScanType] = useState<string>('all');
  const [selectedStatus, setSelectedStatus] = useState<string>('all');
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(12);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [showDuplicateModal, setShowDuplicateModal] = useState(false);
  const [selectedProject, setSelectedProject] = useState<RASTProject | null>(null);
  const [createFormData, setCreateFormData] = useState<CreateProjectData>({
    name: '',
    url: '',
    description: '',
    scanType: 'full',
    authentication: {
      type: 'none'
    }
  });
  const [duplicateFormData, setDuplicateFormData] = useState<DuplicateProjectData>({
    name: '',
    url: ''
  });
  const [totalProjects, setTotalProjects] = useState(0);
  const [totalPages, setTotalPages] = useState(0);
  const [error, setError] = useState<string | null>(null);

  const mockProjects: RASTProject[] = [
    {
      id: 1,
      name: 'E-commerce Web App',
      url: 'https://demo-ecommerce.example.com',
      description: 'Main e-commerce application for online shopping',
      scanType: 'full',
      lastScan: {
        id: 101,
        status: 'COMPLETED',
        timestamp: '2025-08-10T10:30:00Z',
        duration: '3m 45s'
      },
      vulnerabilities: {
        critical: 2,
        high: 4,
        medium: 4,
        low: 2
      },
      securityScore: 75.5,
      createdBy: 'admin@example.com',
      createdAt: '2025-07-15T09:00:00Z'
    },
    {
      id: 2,
      name: 'Admin Portal',
      url: 'https://admin.example.com',
      description: 'Administrative interface for system management',
      scanType: 'active',
      authentication: {
        type: 'form',
        username: 'admin',
        password: '********'
      },
      lastScan: {
        id: 102,
        status: 'RUNNING',
        timestamp: '2025-08-10T09:15:00Z',
        duration: '1m 23s'
      },
      vulnerabilities: {
        critical: 1,
        high: 3,
        medium: 3,
        low: 1
      },
      securityScore: 68.2,
      createdBy: 'admin@example.com',
      createdAt: '2025-07-20T14:30:00Z'
    },
    {
      id: 3,
      name: 'API Gateway',
      url: 'https://api.example.com',
      description: 'REST API gateway for microservices',
      scanType: 'passive',
      lastScan: {
        id: 103,
        status: 'COMPLETED',
        timestamp: '2025-08-10T08:45:00Z',
        duration: '2m 12s'
      },
      vulnerabilities: {
        critical: 0,
        high: 1,
        medium: 2,
        low: 2
      },
      securityScore: 82.1,
      createdBy: 'dev@example.com',
      createdAt: '2025-07-25T11:15:00Z'
    }
  ];

  const fetchProjects = useCallback(async () => {
    try {
      setLoading(true);
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));
      setProjects(mockProjects);
    } catch (error) {
      console.error('Error fetching projects:', error);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchProjects();
  }, [fetchProjects]);

  const handleCreateProject = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      // Mock API call
      const newProject: RASTProject = {
        id: Date.now(),
        ...createFormData,
        vulnerabilities: { critical: 0, high: 0, medium: 0, low: 0 },
        securityScore: 100,
        createdBy: 'current-user@example.com',
        createdAt: new Date().toISOString()
      };

      setProjects(prev => [newProject, ...prev]);
      setShowCreateModal(false);
      setCreateFormData({
        name: '',
        url: '',
        description: '',
        scanType: 'full',
        authentication: { type: 'none' }
      });
    } catch (error) {
      console.error('Error creating project:', error);
    }
  };

  const handleDuplicateProject = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!selectedProject) return;

    try {
      const duplicatedProject: RASTProject = {
        ...selectedProject,
        id: Date.now(),
        name: duplicateFormData.name,
        url: duplicateFormData.url,
        createdAt: new Date().toISOString(),
        vulnerabilities: { critical: 0, high: 0, medium: 0, low: 0 },
        securityScore: 100
      };

      setProjects(prev => [duplicatedProject, ...prev]);
      setShowDuplicateModal(false);
      setSelectedProject(null);
      setDuplicateFormData({ name: '', url: '' });
    } catch (error) {
      console.error('Error duplicating project:', error);
    }
  };

  const handleDeleteProject = async (projectId: number) => {
    if (window.confirm('Are you sure you want to delete this project?')) {
      try {
        setProjects(prev => prev.filter(p => p.id !== projectId));
      } catch (error) {
        console.error('Error deleting project:', error);
      }
    }
  };

  const filteredProjects = projects.filter(project => {
    const matchesSearch = project.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         project.url.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesScanType = selectedScanType === 'all' || project.scanType === selectedScanType;
    const matchesStatus = selectedStatus === 'all' || 
                         (selectedStatus === 'completed' && project.lastScan?.status === 'COMPLETED') ||
                         (selectedStatus === 'running' && project.lastScan?.status === 'RUNNING') ||
                         (selectedStatus === 'failed' && project.lastScan?.status === 'FAILED') ||
                         (selectedStatus === 'pending' && project.lastScan?.status === 'PENDING');
    
    return matchesSearch && matchesScanType && matchesStatus;
  });

  const paginatedProjects = filteredProjects.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  // Update totalPages state when filtered projects change
  useEffect(() => {
    setTotalPages(Math.ceil(filteredProjects.length / itemsPerPage));
  }, [filteredProjects.length, itemsPerPage]);

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <ArrowPathIcon className="w-8 h-8 animate-spin text-blue-600" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <h2 className="text-xl font-semibold text-gray-900">RAST Projects</h2>
        <button
          onClick={() => setShowCreateModal(true)}
          className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-blue-600 hover:bg-blue-700"
        >
          <PlusIcon className="w-4 h-4 mr-2" />
          Create Project
        </button>
      </div>

      {/* Filters and Search */}
      <div className="bg-white p-4 rounded-lg shadow-sm border border-gray-200">
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Search</label>
            <div className="relative">
              <MagnifyingGlassIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
              <input
                type="text"
                placeholder="Search projects..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10 pr-3 py-2 border border-gray-300 rounded-md w-full focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Scan Type</label>
            <select
              value={selectedScanType}
              onChange={(e) => setSelectedScanType(e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="all">All Types</option>
              <option value="full">Full Scan</option>
              <option value="passive">Passive</option>
              <option value="active">Active</option>
              <option value="custom">Custom</option>
            </select>
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Status</label>
            <select
              value={selectedStatus}
              onChange={(e) => setSelectedStatus(e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="all">All Statuses</option>
              <option value="completed">Completed</option>
              <option value="running">Running</option>
              <option value="failed">Failed</option>
              <option value="pending">Pending</option>
            </select>
          </div>
          
          <div className="flex items-end">
            <div className="flex space-x-2">
              <button
                onClick={() => setViewMode('cards')}
                className={`px-3 py-2 rounded-md text-sm font-medium ${
                  viewMode === 'cards'
                    ? 'bg-blue-100 text-blue-700'
                    : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                }`}
              >
                Cards
              </button>
              <button
                onClick={() => setViewMode('list')}
                className={`px-3 py-2 rounded-md text-sm font-medium ${
                  viewMode === 'list'
                    ? 'bg-blue-100 text-blue-700'
                    : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                }`}
              >
                List
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Projects Grid/List */}
      {viewMode === 'cards' ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {paginatedProjects.map((project) => (
            <motion.div
              key={project.id}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              className="bg-white rounded-lg shadow-sm border border-gray-200 p-6 hover:shadow-md transition-shadow"
            >
              <div className="flex items-start justify-between mb-4">
                <div className="flex-1">
                  <h3 className="text-lg font-semibold text-gray-900 mb-2">{project.name}</h3>
                  <p className="text-sm text-gray-600 mb-2">{project.url}</p>
                  {project.description && (
                    <p className="text-sm text-gray-500 mb-3">{project.description}</p>
                  )}
                </div>
                <div className="flex space-x-2">
                  <button
                    onClick={() => setSelectedProject(project)}
                    className="text-gray-400 hover:text-gray-600"
                  >
                    <EyeIcon className="w-4 h-4" />
                  </button>
                  <button
                    onClick={() => setShowDuplicateModal(true)}
                    className="text-gray-400 hover:text-gray-600"
                  >
                    <DocumentDuplicateIcon className="w-4 h-4" />
                  </button>
                  <button
                    onClick={() => handleDeleteProject(project.id)}
                    className="text-red-400 hover:text-red-600"
                  >
                    <TrashIcon className="w-4 h-4" />
                  </button>
                </div>
              </div>

              <div className="space-y-3">
                <div className="flex items-center justify-between text-sm">
                  <span className="text-gray-500">Scan Type:</span>
                  <span className="font-medium text-gray-900 capitalize">{project.scanType}</span>
                </div>
                
                <div className="flex items-center justify-between text-sm">
                  <span className="text-gray-500">Security Score:</span>
                  <span className={`font-medium ${
                    project.securityScore >= 80 ? 'text-green-600' :
                    project.securityScore >= 60 ? 'text-yellow-600' : 'text-red-600'
                  }`}>
                    {project.securityScore}%
                  </span>
                </div>

                {project.lastScan && (
                  <div className="flex items-center justify-between text-sm">
                    <span className="text-gray-500">Last Scan:</span>
                    <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${
                      project.lastScan.status === 'COMPLETED' ? 'bg-green-100 text-green-800' :
                      project.lastScan.status === 'RUNNING' ? 'bg-blue-100 text-blue-800' :
                      project.lastScan.status === 'FAILED' ? 'bg-red-100 text-red-800' :
                      'bg-gray-100 text-gray-800'
                    }`}>
                      {project.lastScan.status.toLowerCase()}
                    </span>
                  </div>
                )}

                <div className="pt-3 border-t border-gray-200">
                  <div className="flex justify-between text-xs text-gray-500 mb-2">
                    <span>Vulnerabilities</span>
                    <span>{project.vulnerabilities.critical + project.vulnerabilities.high + project.vulnerabilities.medium + project.vulnerabilities.low}</span>
                  </div>
                  <div className="flex space-x-1">
                    {project.vulnerabilities.critical > 0 && (
                      <div className="flex-1 bg-red-500 h-2 rounded"></div>
                    )}
                    {project.vulnerabilities.high > 0 && (
                      <div className="flex-1 bg-orange-500 h-2 rounded"></div>
                    )}
                    {project.vulnerabilities.medium > 0 && (
                      <div className="flex-1 bg-yellow-500 h-2 rounded"></div>
                    )}
                    {project.vulnerabilities.low > 0 && (
                      <div className="flex-1 bg-blue-500 h-2 rounded"></div>
                    )}
                  </div>
                </div>
              </div>
            </motion.div>
          ))}
        </div>
      ) : (
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 overflow-hidden">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Project</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Scan Type</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Security Score</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Vulnerabilities</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {paginatedProjects.map((project) => (
                <tr key={project.id} className="hover:bg-gray-50">
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div>
                      <div className="text-sm font-medium text-gray-900">{project.name}</div>
                      <div className="text-sm text-gray-500">{project.url}</div>
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-gray-100 text-gray-800 capitalize">
                      {project.scanType}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    {project.lastScan ? (
                      <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                        project.lastScan.status === 'COMPLETED' ? 'bg-green-100 text-green-800' :
                        project.lastScan.status === 'RUNNING' ? 'bg-blue-100 text-blue-800' :
                        project.lastScan.status === 'FAILED' ? 'bg-red-100 text-red-800' :
                        'bg-gray-100 text-gray-800'
                      }`}>
                        {project.lastScan.status.toLowerCase()}
                      </span>
                    ) : (
                      <span className="text-sm text-gray-500">No scans</span>
                    )}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`text-sm font-medium ${
                      project.securityScore >= 80 ? 'text-green-600' :
                      project.securityScore >= 60 ? 'text-yellow-600' : 'text-red-600'
                    }`}>
                      {project.securityScore}%
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm text-gray-900">
                      {project.vulnerabilities.critical + project.vulnerabilities.high + project.vulnerabilities.medium + project.vulnerabilities.low} total
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                    <div className="flex space-x-2">
                      <button
                        onClick={() => setSelectedProject(project)}
                        className="text-blue-600 hover:text-blue-900"
                      >
                        View
                      </button>
                      <button
                        onClick={() => setShowDuplicateModal(true)}
                        className="text-gray-600 hover:text-gray-900"
                      >
                        Duplicate
                      </button>
                      <button
                        onClick={() => handleDeleteProject(project.id)}
                        className="text-red-600 hover:text-red-900"
                      >
                        Delete
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {/* Pagination */}
      {totalPages > 1 && (
        <div className="flex items-center justify-between">
          <div className="text-sm text-gray-700">
            Showing {((currentPage - 1) * itemsPerPage) + 1} to {Math.min(currentPage * itemsPerPage, filteredProjects.length)} of {filteredProjects.length} results
          </div>
          <div className="flex space-x-2">
            <button
              onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
              disabled={currentPage === 1}
              className="px-3 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              <ChevronLeftIcon className="w-4 h-4" />
            </button>
            <button
              onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
              disabled={currentPage === totalPages}
              className="px-3 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              <ChevronRightIcon className="w-4 h-4" />
            </button>
          </div>
        </div>
      )}

      {/* Create Project Modal */}
      {showCreateModal && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
            <div className="mt-3">
              <h3 className="text-lg font-medium text-gray-900 mb-4">Create New RAST Project</h3>
              <form onSubmit={handleCreateProject} className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Project Name</label>
                  <input
                    type="text"
                    required
                    value={createFormData.name}
                    onChange={(e) => setCreateFormData(prev => ({ ...prev, name: e.target.value }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="Enter project name"
                  />
                </div>
                
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">URL</label>
                  <input
                    type="url"
                    required
                    value={createFormData.url}
                    onChange={(e) => setCreateFormData(prev => ({ ...prev, url: e.target.value }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="https://example.com"
                  />
                </div>
                
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Description</label>
                  <textarea
                    value={createFormData.description}
                    onChange={(e) => setCreateFormData(prev => ({ ...prev, description: e.target.value }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    rows={3}
                    placeholder="Enter project description"
                  />
                </div>
                
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Scan Type</label>
                  <select
                    value={createFormData.scanType}
                    onChange={(e) => setCreateFormData(prev => ({ ...prev, scanType: e.target.value as any }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  >
                    <option value="full">Full Scan</option>
                    <option value="passive">Passive</option>
                    <option value="active">Active</option>
                    <option value="custom">Custom</option>
                  </select>
                </div>
                
                <div className="flex space-x-3 pt-4">
                  <button
                    type="submit"
                    className="flex-1 px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
                  >
                    Create Project
                  </button>
                  <button
                    type="button"
                    onClick={() => setShowCreateModal(false)}
                    className="flex-1 px-4 py-2 bg-gray-300 text-gray-700 rounded-md hover:bg-gray-400 focus:outline-none focus:ring-2 focus:ring-gray-500"
                  >
                    Cancel
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* Duplicate Project Modal */}
      {showDuplicateModal && selectedProject && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
            <div className="mt-3">
              <h3 className="text-lg font-medium text-gray-900 mb-4">Duplicate Project</h3>
              <form onSubmit={handleDuplicateProject} className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">New Project Name</label>
                  <input
                    type="text"
                    required
                    value={duplicateFormData.name}
                    onChange={(e) => setDuplicateFormData(prev => ({ ...prev, name: e.target.value }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="Enter new project name"
                  />
                </div>
                
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">New URL</label>
                  <input
                    type="url"
                    required
                    value={duplicateFormData.url}
                    onChange={(e) => setDuplicateFormData(prev => ({ ...prev, url: e.target.value }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="https://new-example.com"
                  />
                </div>
                
                <div className="flex space-x-3 pt-4">
                  <button
                    type="submit"
                    className="flex-1 px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
                  >
                    Duplicate Project
                  </button>
                  <button
                    type="button"
                    onClick={() => {
                      setShowDuplicateModal(false);
                      setSelectedProject(null);
                    }}
                    className="flex-1 px-4 py-2 bg-gray-300 text-gray-700 rounded-md hover:bg-gray-400 focus:outline-none focus:ring-2 focus:ring-gray-500"
                  >
                    Cancel
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default RASTProjects;
