import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import {
  ArrowLeftIcon,
  PlayIcon,
  ClockIcon,
  ExclamationTriangleIcon,
  CheckCircleIcon,
  XCircleIcon,
  ShieldExclamationIcon,
  ChartBarIcon,
  CogIcon,
  DocumentTextIcon,
  EyeIcon,
  MagnifyingGlassIcon,
  FunnelIcon,
  ChevronDownIcon,
  ChevronUpIcon,
  StarIcon,
  CodeBracketIcon,
  BeakerIcon
} from '@heroicons/react/24/outline';

interface ProjectDetails {
  id: number;
  name: string;
  key: string;
  language: string;
  repositoryUrl?: string;
  branch: string;
  qualityGate: 'PASSED' | 'FAILED' | 'WARNING' | 'NONE';
  maintainabilityRating: 'A' | 'B' | 'C' | 'D' | 'E';
  securityRating: 'A' | 'B' | 'C' | 'D' | 'E';
  reliabilityRating: 'A' | 'B' | 'C' | 'D' | 'E';
  vulnerabilityCount: number;
  bugCount: number;
  codeSmellCount: number;
  securityHotspotCount: number;
  linesOfCode: number;
  coverage: number;
  technicalDebt: number;
  lastAnalysis?: string;
  lastScan?: {
    id: number;
    status: 'COMPLETED' | 'RUNNING' | 'FAILED' | 'PENDING';
    timestamp: string;
    duration?: string;
  };
}

const SASTProjectDetails: React.FC = () => {
  const { projectId } = useParams<{ projectId: string }>();
  const navigate = useNavigate();
  const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';
  
  const [project, setProject] = useState<ProjectDetails | null>(null);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState<'overview' | 'issues' | 'hotspots' | 'quality-gates' | 'coverage' | 'debt'>('overview');
  const [scanning, setScanning] = useState(false);

  useEffect(() => {
    if (projectId) {
      fetchProjectDetails();
    }
  }, [projectId]);

  const fetchProjectDetails = async () => {
    try {
      const response = await fetch(`${API_URL}/sast/projects/${projectId}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const data = await response.json();
        setProject(data);
      } else {
        // Fallback to mock data
        setProject({
          id: parseInt(projectId!),
          name: 'Web Application Security',
          key: 'web-app-sec',
          language: 'JavaScript',
          repositoryUrl: 'https://github.com/example/web-app',
          branch: 'main',
          qualityGate: 'PASSED',
          maintainabilityRating: 'A',
          securityRating: 'B',
          reliabilityRating: 'A',
          vulnerabilityCount: 2,
          bugCount: 5,
          codeSmellCount: 12,
          securityHotspotCount: 3,
          linesOfCode: 15420,
          coverage: 78.5,
          technicalDebt: 2.5,
          lastAnalysis: '2024-01-15T10:30:00Z',
          lastScan: {
            id: 101,
            status: 'COMPLETED',
            timestamp: '2024-01-15T10:30:00Z',
            duration: '2m 34s'
          }
        });
      }
    } catch (error) {
      console.error('Error fetching project details:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleStartScan = async () => {
    if (!project) return;
    
    setScanning(true);
    try {
      const response = await fetch(`${API_URL}/sast/scans`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          project_id: project.id,
          scan_type: 'full',
          branch: project.branch
        })
      });

      if (response.ok) {
        setTimeout(() => {
          fetchProjectDetails();
          setScanning(false);
        }, 2000);
      }
    } catch (error) {
      console.error('Error starting scan:', error);
      setScanning(false);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-full">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  if (!project) {
    return (
      <div className="p-6">
        <div className="text-center">
          <h2 className="text-xl font-semibold text-gray-900">Project not found</h2>
          <p className="text-gray-600 mt-2">The requested project could not be found.</p>
          <button
            onClick={() => navigate('/sast/projects')}
            className="mt-4 inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700"
          >
            <ArrowLeftIcon className="w-4 h-4 mr-2" />
            Back to Projects
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b border-gray-200">
        <div className="px-6 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <button
                onClick={() => navigate('/sast/projects')}
                className="inline-flex items-center p-2 border border-gray-300 rounded-md text-gray-700 hover:bg-gray-50"
              >
                <ArrowLeftIcon className="w-4 h-4" />
              </button>
              <div>
                <h1 className="text-2xl font-bold text-gray-900">{project.name}</h1>
                <p className="text-sm text-gray-600">{project.key} â€¢ {project.language}</p>
              </div>
            </div>
            <div className="flex items-center space-x-3">
              <button
                onClick={handleStartScan}
                disabled={scanning}
                className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700 disabled:opacity-50"
              >
                {scanning ? (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                    Scanning...
                  </>
                ) : (
                  <>
                    <PlayIcon className="w-4 h-4 mr-2" />
                    Start Scan
                  </>
                )}
              </button>
            </div>
          </div>
        </div>

        {/* Tabs */}
        <div className="px-6">
          <nav className="flex space-x-8">
            {[
              { id: 'overview', name: 'Overview', icon: ChartBarIcon },
              { id: 'issues', name: 'Issues', icon: ExclamationTriangleIcon },
              { id: 'hotspots', name: 'Security Hotspots', icon: ShieldExclamationIcon },
              { id: 'quality-gates', name: 'Quality Gates', icon: CheckCircleIcon },
              { id: 'coverage', name: 'Coverage', icon: BeakerIcon },
              { id: 'debt', name: 'Technical Debt', icon: ClockIcon }
            ].map((tab) => (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id as any)}
                className={`flex items-center space-x-2 py-4 px-1 border-b-2 font-medium text-sm ${
                  activeTab === tab.id
                    ? 'border-blue-500 text-blue-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                <tab.icon className="w-4 h-4" />
                <span>{tab.name}</span>
              </button>
            ))}
          </nav>
        </div>
      </div>

      {/* Content */}
      <div className="p-6">
        {activeTab === 'overview' && (
          <ProjectOverview project={project} />
        )}
        {activeTab === 'issues' && (
          <div className="text-center py-12">
            <ExclamationTriangleIcon className="w-12 h-12 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">Issues Management</h3>
            <p className="text-gray-600">Issues list and filtering functionality coming soon...</p>
          </div>
        )}
        {activeTab === 'hotspots' && (
          <div className="text-center py-12">
            <ShieldExclamationIcon className="w-12 h-12 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">Security Hotspots</h3>
            <p className="text-gray-600">Security hotspots management coming soon...</p>
          </div>
        )}
        {activeTab === 'quality-gates' && (
          <div className="text-center py-12">
            <CheckCircleIcon className="w-12 h-12 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">Quality Gates</h3>
            <p className="text-gray-600">Quality gate configuration coming soon...</p>
          </div>
        )}
        {activeTab === 'coverage' && (
          <div className="text-center py-12">
            <BeakerIcon className="w-12 h-12 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">Code Coverage</h3>
            <p className="text-gray-600">Code coverage visualization coming soon...</p>
          </div>
        )}
        {activeTab === 'debt' && (
          <div className="text-center py-12">
            <ClockIcon className="w-12 h-12 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">Technical Debt</h3>
            <p className="text-gray-600">Technical debt analysis coming soon...</p>
          </div>
        )}
      </div>
    </div>
  );
};

// Project Overview Component
const ProjectOverview: React.FC<{ project: ProjectDetails }> = ({ project }) => {
  const getRatingColor = (rating: string) => {
    switch (rating) {
      case 'A': return 'text-green-600 bg-green-100';
      case 'B': return 'text-blue-600 bg-blue-100';
      case 'C': return 'text-yellow-600 bg-yellow-100';
      case 'D': return 'text-orange-600 bg-orange-100';
      case 'E': return 'text-red-600 bg-red-100';
      default: return 'text-gray-600 bg-gray-100';
    }
  };

  return (
    <div className="space-y-6">
      {/* Quality Gate Status */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
        <h2 className="text-lg font-semibold text-gray-900 mb-4">Quality Gate Status</h2>
        <div className="flex items-center space-x-4">
          <div className={`px-4 py-2 rounded-full text-sm font-medium ${
            project.qualityGate === 'PASSED' ? 'text-green-600 bg-green-100' :
            project.qualityGate === 'FAILED' ? 'text-red-600 bg-red-100' :
            'text-yellow-600 bg-yellow-100'
          }`}>
            {project.qualityGate}
          </div>
          <span className="text-sm text-gray-600">
            Last analysis: {project.lastAnalysis ? new Date(project.lastAnalysis).toLocaleDateString() : 'Never'}
          </span>
        </div>
      </div>

      {/* Metrics Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {/* Maintainability */}
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Maintainability</p>
              <p className="text-2xl font-bold text-gray-900">{project.maintainabilityRating}</p>
            </div>
            <div className={`px-3 py-1 rounded-full text-sm font-medium ${getRatingColor(project.maintainabilityRating)}`}>
              {project.maintainabilityRating}
            </div>
          </div>
        </div>

        {/* Security */}
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Security</p>
              <p className="text-2xl font-bold text-gray-900">{project.securityRating}</p>
            </div>
            <div className={`px-3 py-1 rounded-full text-sm font-medium ${getRatingColor(project.securityRating)}`}>
              {project.securityRating}
            </div>
          </div>
        </div>

        {/* Reliability */}
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Reliability</p>
              <p className="text-2xl font-bold text-gray-900">{project.reliabilityRating}</p>
            </div>
            <div className={`px-3 py-1 rounded-full text-sm font-medium ${getRatingColor(project.reliabilityRating)}`}>
              {project.reliabilityRating}
            </div>
          </div>
        </div>

        {/* Coverage */}
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Coverage</p>
              <p className="text-2xl font-bold text-gray-900">{project.coverage}%</p>
            </div>
            <div className="w-12 h-12 relative">
              <svg className="w-12 h-12 transform -rotate-90" viewBox="0 0 36 36">
                <path
                  d="M18 2.0845 a 15.9155 15.9155 0 0 1 0 31.831 a 15.9155 15.9155 0 0 1 0 -31.831"
                  fill="none"
                  stroke="#e5e7eb"
                  strokeWidth="2"
                />
                <path
                  d="M18 2.0845 a 15.9155 15.9155 0 0 1 0 31.831 a 15.9155 15.9155 0 0 1 0 -31.831"
                  fill="none"
                  stroke="#3b82f6"
                  strokeWidth="2"
                  strokeDasharray={`${project.coverage}, 100`}
                />
              </svg>
            </div>
          </div>
        </div>
      </div>

      {/* Issues Summary */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
        <h2 className="text-lg font-semibold text-gray-900 mb-4">Issues Summary</h2>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          <div className="text-center">
            <div className="text-2xl font-bold text-red-600">{project.vulnerabilityCount}</div>
            <div className="text-sm text-gray-600">Vulnerabilities</div>
          </div>
          <div className="text-center">
            <div className="text-2xl font-bold text-orange-600">{project.bugCount}</div>
            <div className="text-sm text-gray-600">Bugs</div>
          </div>
          <div className="text-center">
            <div className="text-2xl font-bold text-yellow-600">{project.codeSmellCount}</div>
            <div className="text-sm text-gray-600">Code Smells</div>
          </div>
          <div className="text-center">
            <div className="text-2xl font-bold text-blue-600">{project.securityHotspotCount}</div>
            <div className="text-sm text-gray-600">Security Hotspots</div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SASTProjectDetails;
