import React, { useState } from 'react';
import { ChartBarIcon, FolderIcon } from '@heroicons/react/24/outline';
import RASPOverview from '../../components/RASP/RASPOverview';
import RASPProjects from '../../components/RASP/RASPProjects';

const RASP: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'overview' | 'projects'>('overview');

  const tabs = [
    { id: 'overview', label: 'Overview', icon: <ChartBarIcon className="w-4 h-4" /> },
    { id: 'projects', label: 'Projects', icon: <FolderIcon className="w-4 h-4" /> }
  ] as const;

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Runtime Application Self-Protection (RASP)</h1>
          <p className="text-gray-600">Projects and overview for runtime application protection</p>
        </div>
      </div>

      <div className="border-b border-gray-200">
        <nav className="-mb-px flex space-x-8">
          {tabs.map((tab) => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              className={`flex items-center py-2 px-1 border-b-2 font-medium text-sm ${
                activeTab === tab.id
                  ? 'border-blue-500 text-blue-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              }`}
            >
              {tab.icon}
              <span className="ml-2">{tab.label}</span>
            </button>
          ))}
        </nav>
      </div>

      <div className="mt-6">
        {activeTab === 'overview' && <RASPOverview />}
        {activeTab === 'projects' && <RASPProjects />}
      </div>
    </div>
  );
};

export default RASP;
